import React,{Component} from 'react';
import socketIOClient from "socket.io-client";

const ENDPOINT = "http://127.0.0.1:4000";


class Screen extends  Component
{

    constructor(props)
    {
        super(props);

        this.state ={
            altitude: '',
            HIS : '',
            ADI : ''
        };

        console.log('Constructor');
        
    }

    componentDidMount(){
        console.log('Component');
        const socket = socketIOClient(ENDPOINT);
        socket.on("Data", data => {
            this.setState({altitude : data[0],HIS : data[1],ADI : data[2]});
        });
    }

    render()
    {
        console.log('render');
        return(

            <div>
               <h1>Hello </h1>
                <p>{this.state.altitude}</p>

                <p>{this.state.HIS}</p>

                <p>{this.state.ADI}</p>
            </div>

        )
    }


}

export default Screen;
