import React,{Component} from 'react';
import socketIOClient from "socket.io-client";
import  '../Style/Style.css'

const ENDPOINT = "http://127.0.0.1:4000";


class Screen extends  Component
{

    constructor(props)
    {
        super(props);

        this.state ={
            altitude: '',
            HIS : '',
            ADI : '',
            visual : 'graphic'
        };

        console.log('Constructor');
        
    }

    componentDidMount(){
        console.log('Component');
        const socket = socketIOClient(ENDPOINT);
        socket.on("Data", data => {
            this.setState({altitude : data[0],HIS : data[1],ADI : data[2]});
        });
    }

    change_visualToText()
    {
       this.setState({visual : 'text'});
    }


    change_visualToGraphic()
    {
        this.setState({visual : 'graphic'});
    }
    render()
    {

        let blue = "50%";
        let green = "50%";


        console.log('render');
        const data = this.state;


        if(data.ADI > 0  ) {
            blue = (data.ADI / 2 + 53).toString() + "%";
            green = (100 - (data.ADI / 2 + 47)).toString() + "%";
        }
        else
        {
            green = ((-data.ADI) / 2 + 53).toString() + "%" ;
            blue = (100 - ((-data.ADI) / 2 + 47)).toString() + "%";
        }


        console.log(data.ADI);


        let text = "hidden";
        let graphic = "visible";


        let altitude = (300 -data.altitude/10).toString()+"px";

        console.log(altitude);

        return(

            <div>

               <h1>Hello </h1>

                <div className="container">
                    <div className="button_container">
                        <button id="text_btn"  onClick={this.change_visualToText.bind(this)}  >Text</button>
                        <button id="visual_btn"  onClick={this.change_visualToGraphic.bind(this)} >Visual</button>
                    </div>
                    <div className="text_container" style={{ display : this.state.visual == 'text' ? "flex" : "none"}}>

                        <p> Altitude : M </p>
                        <p> </p>
                        <h2 > HIS: {this.state.HIS}  </h2>

                        <h2 > ADI: {this.state.ADI}</h2>
                    </div>
                    <div className="graphic_container" style={ { display : this.state.visual == 'graphic' ? "flex" : "none"} }>
                        <div className="altitude_container">
                          <div className="bar">
                             <hr style = {{ marginTop:  altitude }}/>
                          </div>
                        </div>
                        <div className="HIS_container"></div>
                        <div className="ADI_container">
                            <div className="circle" >
                                <div className="green" style={{ width : green}}></div>
                                <div className="blue" style={{ width : blue}}></div>
                             </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }


}

export default Screen;
